create table MOCK_WINE_DATA (
	wine_id INT,
	wine_name VARCHAR(64),
	country VARCHAR(64),
	region VARCHAR(64),
	winery VARCHAR(64),
	wine_description VARCHAR(8192),
	winegrape_variety VARCHAR(64),
	bottled_year INT,
	price DECIMAL(6,2),
	rating DECIMAL(2,1),
	food_pairing VARCHAR(64),
	serving_temperature DECIMAL(4,1),
	tasting_notes VARCHAR(64),
	wine_color VARCHAR(64),
	body VARCHAR(16),
	acidity VARCHAR(16),
	tannins VARCHAR(16),
	finish VARCHAR(16),
	oakiness VARCHAR(16),
	sweetness VARCHAR(16)
);

